sections.css:
html{
	font-size:16px;
}

// Color
body{
	font-family:  'Helvetica Neue', Arial, sans-serfi;
	font-size: 1rem;
}

.btn{
	padding: .5rem 1rem;
	display:inline-block;
	transition: all .2s ease-in-out;
	&--primary,
	&--secondary{
	  color:#FFF;
		&:hover,
		&:active{
			color:#FFF;
		}
	}
	&--primary{
		background-color: $brand-primary;
		border-color: $brand-primary;
		&:hover{
			background-color: darken($brand-primary, 10%);
			border-color: darken($brand-primary, 15%);
		}
	}
	&--secondary{
		background-color:$brand-secondary;
		border-color: $brand-secondary;
		&:hover{
			background-color: darken($brand-secondary, 10%);
			border-color: darken($brand-secondary, 15%);
		}
	}
}

/* Sticky footer styles
-------------------------------------------------- */
html {
	position: relative;
	min-height: 100%;
}
body {
	margin-bottom: 60px;
}
.footer {
	position: absolute;
	bottom: 0;
	width: 100%;
	height: 60px;
	background-color: #f5f5f5;
}
.container .text-muted {
	margin: 20px 0;
}

.page-content {
	margin-top: 60px;
}

